import styled from "styled-components";

const Button = styled.button`
  background-color: #282c34;
  color: white;
  font-size: 16px;
  padding: 10px 10px;
  border-radius: 10px;
  margin: 10px 0px;
  cursor: pointer; 
  margin-bottom: 1.25em;
`;

const buttonHit = option => () =>{
    if (option == "Happy"){
        alert(option);
    }
    if (option == "Angry"){
        alert(option);
    }
    if (option == "Sad"){
        alert(option);
    }
    if (option == "Depressed"){
        alert(option);
    }
    if (option == "Overwhelmed"){
        alert(option);
    }
    if (option == "Stressed"){
        alert(option);
    }
    if (option == "Scared"){
        alert(option);
    }
    if (option == "Disgusted"){
        alert(option);
    }
    if (option == "Nervous"){
        alert(option);
    }
    if (option == "In Love"){
        alert(option);
    }
    if (option == "Confused"){
        alert(option);
    }
};

export {Button};
export default buttonHit;
